  app.post("/api/auth/login-pin", async (req: any, res: any) => {
    try {
      const { email, pin } = req.body;

      // Validate PIN
      if (!pin || pin.length !== 6 || !/^\d{6}$/.test(pin)) {
        return res.status(400).json({
          success: false,
          message: "PIN must be exactly 6 digits"
        });
      }

      // Find user
      const user = await storage.getUserByEmail(email);
      if (!user) {
        return res.status(401).json({
          success: false,
          message: "Invalid email or PIN"
        });
      }

      if (!user.pinHash) {
        return res.status(401).json({
          success: false,
          message: "Invalid email or PIN"
        });
      }

      // Verify PIN - compare against pinHash, not password
      const isValidPin = await bcrypt.compare(pin, user.pinHash);
      if (!isValidPin) {
        return res.status(401).json({
          success: false,
          message: "Invalid email or PIN"
        });
      }

      // Generate JWT token
      const token = jwt.sign(
        { userId: user.id, id: user.id, email: user.email, role: user.role },
        JWT_SECRET,
        { expiresIn: '7d' }
      );

      res.json({
        success: true,
        message: "Login successful",
        token,
        user: {
          id: user.id,
          username: user.username,
          email: user.email,
          role: user.role,
          balance: user.balance
        }
      });
    } catch (error: any) {
      console.error("Login error:", error);
      res.status(500).json({
        success: false,
        message: "Login failed"
      });
    }
  });

